import styled from 'styled-components'
export const DestacadosWrapper = styled.div`
  width:100%;
height:30vh;
`;
export const BoxResto = styled.div`
width:5vw;
height:5vw;
background-image:url('/${props => props.background}.jpg');
background-size:contain;
background-position:center;
border-radius:100%;
border:3px solid #b2c5f9;
@media (max-width: 600px) {
width:20vw;
height:20vw;
}
`;
export const TitleDes = styled.h4`
  font-family:'Karla', sans-serif;
  color: #333;

`;
export const LocalesWrapper = styled.div`
width:40vw;
height:50vw; 
background:dodgerblue;
@media (max-width: 600px) {
    width:30vw;
    height:50vw;
    }
`;
export const LocalWrapp = styled.div`
  width:40vw;
  height:30vw;
  background-image:url('/${props => props.background}.jpg');
  background-size:contain;
background-position:center;
@media (max-width: 600px) {
    width:30vw;
    height:20vw;
    }
`;
