import styled from 'styled-components'
export const DestacadosWrapper = styled.div`
  width:100%;
height:30vh;
`;
export const BoxResto = styled.div`
width:5vw;
height:5vw;
background-image:url('/${props => props.background}.jpg');
background-size:contain;
background-position:center;
border-radius:100%;
border:3px solid #b2c5f9;
@media (max-width: 600px) {
width:20vw;
height:20vw;
}
`;
export const TitleDes = styled.h4`
  font-family:'Karla', sans-serif;
  color: #333;

`;
export const LocalesWrapper = styled.div`
width:40vw;
height:50vw; 
background:#cbe3ef;
border-top-left-radius:6px;
border-top-right-radius:6px;
@media (max-width: 600px) {
    width:40vw;
    height:50vw;
    }
`;
export const LocalWrapp = styled.div`
  width:40vw;
  height:30vw;
border-top-left-radius:6px;
border-top-right-radius:6px;
  background-image:url('/${props => props.background}.jpg');
  background-size:contain;
background-position:center;
@media (max-width: 600px) {
    width:40vw;
    height:30vw;
    }
`;
export const Pe = styled.p`
  font-family:'Karla',sans-serif;
  color:#333;
  padding:0 2vw 0 2vw;
  margin:0;
`;
