import styled from 'styled-components'

export const ComercioWrapper = styled.div`
  width:100%;
  height:100vh;
  padding:18% 0% 4% 0%;
`;
export const PortadaComercio = styled.div`
  background-image:url('/bar4.jpg');
  background-position:center;
  background-size:cover;
  width:100%;
  height:30vh;
  @media (max-width: 600px) {
    
    }
`;
export const Carta = styled.div`
width:100%;
padding:2vh 0 2vh 0;
`;

export const ItemCarta = styled.div`
  background:#9fffce;
  border-radius:30px;
  width:auto;
  height:4vh;
  text-align:center;
  padding:10px;
  font-family:'Karla', sans-serif;
  color: #333;
  font-weight:600;
  margin:6px;
`;
export const CartaList = styled.div`
  width:100%;
  height:70vh;
  padding:6px;
  border-radius:5px;

`;
export const ItemProduct = styled.div`
  display:grid;
  margin:2% 0 2% 0;
  background: #b2c5f9;
  grid-template-columns:50% 40%;
  align-items:center;
  padding:3% 0 3% 0;
`;
export const ItemNamePrice = styled.div`
display:grid;
grid-template-rows:1fr 1fr;
grid-row-gap:5px;
`;
export const ItemAdd = styled.div`
display:grid;
grid-template-columns:18% 1fr 10%;
align-items:center;
grid-column-gap:1vw;
`;
export const ProductImg = styled.img`
  width:40vw;
`;
export const RestButton = styled.a`
background: #fff;
padding: 2px;
font-size:1.4rem;
line-height:1.2;
color: #1f1f1f;
font-family: 'Karla',sans-serif;
height: 5vh;
width: 5vh;
border-radius: 100%;
font-weight:600;
text-align: center;
-webkit-box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
`;

export const AddButton = styled.a`
background: #fff;
padding: 2px;
color: #1f1f1f;
font-size:1.4rem;
line-height:1.2;
font-family: 'Karla',sans-serif;
height: 5vh;
width: 5vh;
border-radius: 100%;
text-align: center;
font-weight:600;
-webkit-box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
-moz-box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
box-shadow: 0px 3px 5px 0px rgba(0,0,0,0.75);
`;

export const ItemName = styled.p`
  font-family:'Karla', sans-serif;
  color:#333;
  font-weight:500;
  margin:0;
`;
export const ItemPrice = styled.p`
  font-family:'Karla', sans-serif;
  color:#333;
  font-weight:600;
  margin:0;
`;
